import styled from "styled-components";
import Background from "../image/Background.webp";

const About = () => {
  return (
    <AboutWrap>
      <div className="content">
        <p>STUDIO CYLO</p>
        <h3>Stay hungry</h3>
        <h3>Stay foolish</h3>
      </div>
    </AboutWrap>
  );
};

const AboutWrap = styled.section`
  width: 100%;
  min-height: 100vh;
  background-image: url(${Background});
  padding: 10rem 24px 0;
  ::before {
    content: "";
    display: block;
    position: fixed;
    inset: 0;
    background-color: #000;
    opacity: 0.4;
  }
  .content {
    position: relative;
  }
  .body {
    margin: 0;
    padding: 0;
    background: #000fff;
    color: transparent;
    width: 100%;
    height: 100vh;
    overflow: hidden;
    .word {
      position: absolute;
      cursor: grab;
      font-size: 30px;
      color: #ffeb3b;
    }
    .word.highlighted {
      font-weight: bold;
      color: black;
    }
  }
`;
export default About;
