};

const AboutWrap = styled.article`
  display: flex;
  flex-direction: column;
  min-height: calc(100vh - 8rem);
  justify-content: center;
  align-items: center;
  .flexWrap {
    display: flex;
    width: 100%;
    .imgWrap {
      flex-basis: 40%;
      border-radius: 10px;
      overflow: hidden;
      > img {
        max-width: 100%;
      }
    }
  }
  .profile {
    flex-basis: 60%;
    padding-left: 10rem;
    h2 {
      font-size: var(--big-font);
      margin-bottom: 1.5rem;
    }
    .sub-title {
      font-size: var(--sub-head-font);
      font-weight: 600;
      color: var(--gray-color);
      margin-bottom: 5rem;
      span {
        color: var(--point-color);
      }
    }
    .desc {
        color:var(--gray-color);
        line-height: 150%;
    }
  }
`;
export default About;
