import styled from "styled-components";
import Background from "../image/Background.webp";
import { useEffect, useRef } from "react";
import Matter from "matter-js";

const About = () => {
  const textNode = useRef<HTMLDivElement | null>(null);
  useEffect(() => {
    splitWords();
    renderCanvas();
  }, []);

  const splitWords = () => {
    if (!textNode.current) return;
    console.log("test")
    const text = textNode.current.textContent;
    const newDomElements = text?.split(" ");
    if (newDomElements) {
      textNode.current.textContent = newDomElements?.join("");
    }
  };

  const renderCanvas = () => {
    const Engine = Matter.Engine;
    const Render = Matter.Render;
    const World = Matter.World;
    const Bodies = Matter.Bodies;
    const Runner = Matter.Runner;
    const params = {
      isStatic: true,
      render: {
        fillStyle: "transparent",
      },
    };
    const canvasSize = {
      width: window.innerWidth,
      height: window.innerHeight,
    };
    const engine = Engine.create({});

    const render = Render.create({
      element: document.body,
      engine: engine,
      options: {
        ...canvasSize,
        background: "transparent",
        wireframes: false,
      },
    });
    const floor = Bodies.rectangle(
      canvasSize.width / 2,
      canvasSize.height,
      canvasSize.width,
      50,
      params
    );
    const wall1 = Bodies.rectangle(
      0,
      canvasSize.height / 2,
      50,
      canvasSize.height,
      params
    );
    const wall2 = Bodies.rectangle(
      canvasSize.width,
      canvasSize.height / 2,
      50,
      canvasSize.height,
      params
    );
    const top = Bodies.rectangle(
      canvasSize.width / 2,
      0,
      canvasSize.width,
      50,
      params
    );

    const mouse = Matter.Mouse.create(document.body);
    const mouseConstraint = Matter.MouseConstraint.create(engine, {
      mouse,
      constraint: {
        stiffness: 0.2,
        render: {
          visible: false,
        },
      },
    });

    World.add(engine.world, [floor, wall1, wall2, top, mouseConstraint]);
    render.mouse = mouse;
    Runner.run(engine);
    Render.run(render);

    (function rerender() {
      Matter.Engine.update(engine);
      requestAnimationFrame(rerender);
    })();
  };

  return (
    <AboutWrap>
      <div className="content">
        <p>STUDIO CYLO</p>
        <h3>Stay hungry</h3>
        <h3>Stay foolish</h3>
        <div className="body">
          <div className="text" ref={textNode}>
            software developer with over 9 years of experience, I have developed
            a strong foundation in crafting innovative and efficient technology
            solutions. My passion for technology and entrepreneurship led me to
            co-found Mythrill, where I currently serve as the CTO. I am proud to
            be recognized as one of the "30under30" Armenians in Tech and am
            constantly driven to push boundaries and make a positive impact in
            the industry. When I'm not coding, I enjoy exploring my creative
            side through art, music, and nature
          </div>
        </div>
      </div>
    </AboutWrap>
  );
};

const AboutWrap = styled.section`
  width: 100%;
  min-height: 100vh;
  background-image: url(${Background});
  padding: 10rem 24px 0;
  ::before {
    content: "";
    display: block;
    position: fixed;
    inset: 0;
    background-color: #000;
    opacity: 0.4;
  }
  .content {
    position: relative;
  }
  .body {
    margin: 0;
    padding: 0;
    background: #000fff;
    color: transparent;
    width: 100%;
    height: 100vh;
    overflow: hidden;
    .word {
      position: absolute;
      cursor: grab;
      font-size: 30px;
      color: #ffeb3b;
    }
    .word.highlighted {
      font-weight: bold;
      color: black;
    }
  }
`;
export default About;
